import React, { Component } from 'react';
import Navbar from '../Navbar/Navbar';
import Axios from 'axios'

class Researcher extends Component {
    constructor() {
        super()
        this.state = {
            researchers : [],
            loading: false
        }
    }


    async componentDidMount() {



        axios.get('http://localhost:9000/Researchers')
        .then((response)=>{
          const researchers = response.data.map(obj => obj.data);
            this.setState({ researchers });
          })
        .catch((err)=>{

        })
    }

    render() {

                if (this.state.researchers && this.state.researchers.length ){
                    return  this.state.researchers.map( (chercheur, index) => {
                        return (
                            <div key={index}>

                                <h3>{chercheur.researcherSurname}</h3>
                            </div>
                        );
                    })
                } 
                return (
                    <div>
                        <h1>Telechargement data</h1>
                    </div>
                )


    }

};

export default Researcher;
