const colorArray = ["red", "lightblue", "pink", "yellow", "brown", "magenta"];

class ColorSwitcher extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "indianred"
    };
  }

  /**
   * Uncomment the componentDidMount() method to make the colours auto cycle.
   **/

  // componentDidMount() {
  //   let colorPos = 0;
  //   setInterval(() => {
  //     if(colorArray.length - 1 > colorPos) {
  //       this.setState({
  //         color : colorArray[colorPos]
  //       });
  //       colorPos++;
  //     } else {
  //       this.setState({
  //         color : colorArray[colorPos]
  //       });
  //       colorPos = 0;
  //     }
  //   }, 1000)
  // }

  changeColor(e) {
    this.setState({
      color: e.target.value
    });
  }

  render() {
    const stylesObj = {
      background: this.state.color
    };

    return (
      <div style={stylesObj} className="container">
        <div className="box">
          <h2>Hello, CodePen!</h2>
          <p>
            Set a background colour <small>(Name or #HEX value)</small>
          </p>
          <p>
            Uncomment the componentDidMount() method to make the colours array auto
            cycle.
          </p>
          <br />
          <input
            value={this.state.color}
            onChange={this.changeColor.bind(this)}
          />
          <br />
          <br />
          <br />
          <br />
          <br />
          <small>
            Made with â™¥ by{" "}
            <a href="https://github.com/ajaykarwal" target="_blank">
              Ajay Karwal
            </a>
          </small>
        </div>
      </div>
    );
  }
}

ReactDOM.render(<ColorSwitcher />, document.getElementById("App"));
